mapper.select:
	select a.*, filename as file
	from tbl_user a
 	where uid = #{uid}

select a.*, filename as file
from tbl_user a
where uid= #{uid} and upw = #{upw}

select request
from tbl_user
where user=#{user}

mapper.update:
update tbl_user 
set request=#{request} 
where uid=#{uid}

update tbl_user 
set ulevel = #{ulevel}
where uid=#{uid}

mapper.select:
select uid, umail
from tbl_user
where uid=#{uid}
and umail=#{umail}

mapper.update:
update tbl_user 
set upw=#{upw}
where uid=#{uid}
and umail=#{umail}
